mapper.select:
select
`role_id`,`role_name`,`role_sign`,`remark`,`user_id_create`,`gmt_create`,`gmt_modified`
from oauth_sys_role

    and role_id = #{roleId}
    and role_name = #{roleName}
    and role_sign = #{roleSign}
    and remark = #{remark}
    and user_id_create = #{userIdCreate}



        order by ${sort} ${order}


        order by role_id desc



    limit #{offset}, #{limit}

select count(*) from oauth_sys_role

    and role_id = #{roleId}
    and role_name = #{roleName}
    and role_sign = #{roleSign}
    and remark = #{remark}
    and user_id_create = #{userIdCreate}
